  -- Logistice
  v_ResultStr VARCHAR2(2000):='';
  v_Message VARCHAR2(2000):='';
  v_Result NUMBER:=1; -- 0=failure
  v_Record_ID NUMBER;
  v_AD_User_ID NUMBER;
  -- Parameter
  TYPE RECORD IS REF CURSOR;
    Cur_Parameter RECORD;
    -- Parameter Variables
    --
    v_Languages NUMBER:=0;
    v_Created NUMBER:=0;
    v_rowcount NUMBER;
  BEGIN
    --  Update AD_PInstance
    DBMS_OUTPUT.PUT_LINE('Updating PInstance - Processing ' || p_PInstance_ID) ;
    v_ResultStr:='PInstanceNotFound';
    AD_UPDATE_PINSTANCE(p_PInstance_ID, NULL, 'Y', NULL, NULL) ;
  BEGIN --BODY
    -- Get Parameters
    v_ResultStr:='ReadingParameters';
    FOR Cur_Parameter IN
      (SELECT i.Record_ID, i.AD_User_ID, p.ParameterName, p.P_String, p.P_Number, p.P_Date
      FROM AD_PInstance i
      LEFT JOIN AD_PInstance_Para p
        ON i.AD_PInstance_ID=p.AD_PInstance_ID
      WHERE i.AD_PInstance_ID=p_PInstance_ID
      ORDER BY p.SeqNo
      )
    LOOP
      v_Record_ID:=Cur_Parameter.Record_ID;
      v_AD_User_ID:=Cur_Parameter.AD_User_ID;
    END LOOP; -- Get Parameter
    DBMS_OUTPUT.PUT_LINE('  Record_ID=' || v_Record_ID) ;
    ----------------------------------------------------------------------
    UPDATE AD_Language  SET IsSystemLanguage='N'  WHERE IsBaseLanguage='Y';
    v_rowcount:=SQL%ROWCOUNT;
    DBMS_OUTPUT.PUT_LINE('Base Language = ' || v_rowcount) ;
    SELECT COUNT(*)
    INTO v_Languages
    FROM AD_Language
    WHERE(IsActive='N' OR IsSystemLanguage='N') AND IsBaseLanguage='N';
    DBMS_OUTPUT.PUT_LINE('Number of inactive Languages=' || v_Languages) ;
    SELECT COUNT(*)
    INTO v_Languages
    FROM AD_Language
    WHERE IsActive='Y'  AND IsSystemLanguage='Y';
    DBMS_OUTPUT.PUT_LINE('Number of active Languages=' || v_Languages) ;
    -- Desktop
    INSERT
    INTO AD_Desktop_Trl
      (
        AD_Desktop_ID, AD_Language, Name, Description,
        Help, IsTranslated, Createdby, UpdatedBy,
        AD_Client_ID, AD_Org_ID
      )
    SELECT AD_Desktop_ID, AD_Language, AD_Desktop.Name, AD_Desktop.Description, AD_Desktop.Help, 'N', 0, 0, 0, 0
    FROM AD_Desktop, AD_Language
    WHERE AD_Language.IsActive='Y'  AND AD_Language.IsSystemLanguage='Y'  AND(AD_Desktop_ID, AD_Language) NOT IN
      (SELECT AD_Desktop_ID, AD_Language FROM AD_Desktop_Trl)
      ;
    v_rowcount:=SQL%ROWCOUNT;
    v_Created:=v_Created + v_rowcount;
    DBMS_OUTPUT.PUT_LINE('- AD_Desktop ' || v_rowcount) ;
    -- Element
    INSERT
    INTO AD_Element_Trl
      (
        AD_Element_ID, AD_Language, Name, PrintName,
        Description, Help, PO_Name, PO_PrintName,
        PO_Description, PO_Help, IsTranslated, Createdby,
        UpdatedBy, AD_Client_ID, AD_Org_ID
      )
    SELECT AD_Element_ID, AD_Language, AD_Element.Name, AD_Element.PrintName, AD_Element.Description, AD_Element.Help, AD_Element.PO_Name, AD_Element.PO_PrintName, AD_Element.PO_Description, AD_Element.PO_Help, 'N', 0, 0, 0, 0
    FROM AD_Element, AD_Language
    WHERE AD_Language.IsActive='Y'  AND AD_Language.IsSystemLanguage='Y'  AND(AD_Element_ID, AD_Language) NOT IN
      (SELECT AD_Element_ID, AD_Language FROM AD_Element_Trl)
      ;
    v_rowcount:=SQL%ROWCOUNT;
    v_Created:=v_Created + v_rowcount;
    DBMS_OUTPUT.PUT_LINE('- AD_Element ' || v_rowcount) ;
    -- FieldGroup
    INSERT
    INTO AD_FieldGroup_Trl
