    FROM AD_FieldGroup, AD_Language
    WHERE AD_Language.IsActive='Y'  AND AD_Language.IsSystemLanguage='Y'  AND(AD_FieldGroup_ID, AD_Language) NOT IN
      (SELECT AD_FieldGroup_ID, AD_Language FROM AD_FieldGroup_Trl)
