    v_rowcount:=SQL%ROWCOUNT;
    v_Created:=v_Created + v_rowcount;
    DBMS_OUTPUT.PUT_LINE('- C_Tax ' || v_rowcount) ;
    INSERT
    INTO M_Product_Trl
      (
        M_Product_ID, AD_Language, Name, DocumentNote,
        IsTranslated, Createdby, UpdatedBy, AD_Client_ID,
        AD_Org_ID
      )
    SELECT M_Product_ID, AD_Language, M_Product.Name, M_Product.DocumentNote, 'N', 0, 0, M_Product.AD_Client_ID, M_Product.AD_Org_ID
    FROM M_Product, AD_Language
    WHERE AD_Language.IsActive='Y'  AND AD_Language.IsSystemLanguage='Y'  AND(M_Product_ID, AD_Language) NOT IN
      (SELECT M_Product_ID, AD_Language FROM M_Product_Trl)
      ;
    v_rowcount:=SQL%ROWCOUNT;
    v_Created:=v_Created + v_rowcount;
    DBMS_OUTPUT.PUT_LINE('- M_Product ' || v_rowcount) ;
    ----------------------------------------------------------------------
    INSERT
    INTO AD_TextInterfaces_Trl
      (
        AD_TextInterfaces_ID, AD_Language, Text, IsTranslated,
        Createdby, UpdatedBy, Created, Updated,
        AD_Client_ID, AD_Org_ID, IsActive
      )
    SELECT AD_TextInterfaces_ID, AD_Language, AD_TextInterfaces.Text, 'N', 0, 0, now(), now(), AD_TextInterfaces.AD_Client_ID, AD_TextInterfaces.AD_Org_ID, 'Y'
    FROM AD_TextInterfaces, AD_Language
    WHERE AD_Language.IsActive='Y'  AND AD_Language.IsSystemLanguage='Y'  AND(AD_TextInterfaces_ID, AD_Language) NOT IN
      (SELECT AD_TextInterfaces_ID, AD_Language FROM AD_TextInterfaces_Trl)
      ;
    v_rowcount:=SQL%ROWCOUNT;
    v_Created:=v_Created + v_rowcount;
    DBMS_OUTPUT.PUT_LINE('- AD_TextInterfaces ' || v_rowcount) ;
    -- Summary info
    v_Message:='@Created@ = ' || v_Created;
    -- Commented by cromero 19102006 COMMIT;
    --<<FINISH_PROCESS>>
    --  Update AD_PInstance
    DBMS_OUTPUT.PUT_LINE('Updating PInstance - Finished ' || v_Message) ;
    AD_UPDATE_PINSTANCE(p_PInstance_ID, NULL, 'N', v_Result, v_Message) ;
    RETURN;
  END; --BODY
EXCEPTION
WHEN OTHERS THEN
  v_ResultStr:= '@ERROR=' || SQLERRM;
  DBMS_OUTPUT.PUT_LINE(v_ResultStr) ;
  ROLLBACK;
  AD_UPDATE_PINSTANCE(p_PInstance_ID, NULL, 'N', 0, v_ResultStr) ;
-- Commented by cromero 19102006 RETURN;
END AD_Language_Create
