  v_Amount            NUMBER;
  v_IsDiscountLineAmt CHAR(1) ;
  v_GrandTotal        NUMBER;
  v_TotalLines        NUMBER;
  v_C_PaymentTerm_ID  NUMBER(10) ;
  v_DocDate DATE;
  v_PayDate DATE := SYSDATE;
BEGIN
  SELECT ci.IsDiscountLineAmt,
    i.GrandTotal,
    i.TotalLines,
    i.C_PaymentTerm_ID,
    i.DateInvoiced
  INTO v_IsDiscountLineAmt,
    v_GrandTotal,
    v_TotalLines,
    v_C_PaymentTerm_ID,
    v_DocDate
  FROM AD_CLIENTINFO ci,
    C_INVOICE i
  WHERE ci.AD_Client_ID = i.AD_Client_ID
    AND i.C_Invoice_ID = p_C_Invoice_ID;
  -- What Amount is the Discount Base?
  IF(v_IsDiscountLineAmt = 'Y') THEN
    v_Amount := v_TotalLines;
  ELSE
    v_Amount := v_GrandTotal;
  END IF;
  -- Anything to discount?
  IF(v_Amount = 0) THEN
    RETURN 0;
  END IF;
  IF(p_PayDate IS NOT NULL) THEN
    v_PayDate := p_PayDate;
  END IF;
  -- return discount amount
  RETURN C_Paymentterm_Discount(v_Amount, v_C_PaymentTerm_ID, v_DocDate, p_PayDate) ;
  -- Most likely if invoice not found
EXCEPTION
WHEN OTHERS THEN
  RETURN NULL;
END C_Invoice_Discount_Old
