  v_ResultStr VARCHAR2(2000):='';
  v_ExistsNegativeStock NUMBER;
  v_Stock CHAR:='N';
  v_Name VARCHAR2(60) ;
BEGIN
  v_ResultStr:='Checking stock';
  p_Result:=1;
  SELECT ALLOWNEGATIVESTOCK
  INTO v_Stock
  FROM AD_CLIENTINFO
  WHERE AD_CLIENT_ID=p_AD_Client_ID;
  IF v_Stock='N' THEN
    SELECT COUNT(*)
    INTO v_ExistsNegativeStock
    FROM
      ( SELECT ROUND(COALESCE(QTYONHAND, 0), 2) AS QTY,
        ROUND(COALESCE(QTYORDERONHAND, 0), 2) AS QTYORDER
      FROM M_STORAGE_DETAIL
      WHERE M_PRODUCT_ID=p_M_Product_ID
        AND AD_CLIENT_ID=p_AD_Client_ID
        AND AD_ORG_ID=p_AD_Org_ID
      )
      A
    WHERE QTY<0
      OR QTYORDER<0;
    IF v_ExistsNegativeStock<>0 THEN
      p_Result:=0;
      p_Message:='@NotEnoughStocked@';
    END IF;
  END IF;
  RETURN;
EXCEPTION
WHEN OTHERS THEN
  v_ResultStr:= '@ERROR=' || SQLERRM;
  DBMS_OUTPUT.PUT_LINE(v_ResultStr) ;
  DECLARE
    v_Code NUMBER:=SQLCode;
  BEGIN
    INSERT
    INTO DBA_ERRORLOG
      (
        DBA_ErrorLog_ID, Created,
        Code, Msg,
        Info
      )
      VALUES
      (
        DBA_ErrorLog_Seq.NEXTVAL, now(),
        v_Code, v_ResultStr,
         'M_Check_Stock'
      )
      ;
  END;
  RAISE;
  RETURN;
END M_Check_Stock
