vID NUMBER;
BEGIN -- Default Accounts for all AcctSchema
IF (:NEW.C_SALARY_CATEGORY_ID IS NOT NULL AND :NEW.ISEMPLOYEE ='Y' AND ((:OLD.C_SALARY_CATEGORY_ID IS NULL AND :NEW.C_SALARY_CATEGORY_ID IS NOT NULL) OR :OLD.C_SALARY_CATEGORY_ID <> :NEW.C_SALARY_CATEGORY_ID)) THEN AD_SEQUENCE_NEXT('C_BP_SALCATEGORY', 1000000, vId);
INSERT INTO C_BP_SALCATEGORY (C_BP_SALCATEGORY_ID, AD_CLIENT_ID,AD_ORG_ID,ISACTIVE,CREATED,CREATEDBY, UPDATED,UPDATEDBY,C_BPARTNER_ID,C_SALARY_CATEGORY_ID,DATEFROM) VALUES (vId, :NEW.AD_CLIENT_ID, :NEW.AD_ORG_ID, 'Y', now(), :NEW.UPDATEDBY, now(), :NEW.UPDATEDBY, :NEW.C_BPARTNER_ID, :NEW.C_SALARY_CATEGORY_ID, now()); END IF;
END C_BPartner_Trg2
