 COALESCE(:NEW.CHARGEAMT, 0)) OR(COALESCE(:OLD.M_PRICELIST_ID, 0) <> COALESCE(:NEW.M_PRICELIST_ID, 0)) OR(COALESCE(:OLD.AD_USER_ID, 0) <> COALESCE(:NEW.AD_USER_ID, 0)) OR(COALESCE(:OLD.AD_ORGTRX_ID, 0) <> COALESCE(:NEW.AD_ORGTRX_ID, 0)) OR(COALESCE(:OLD.USER1_ID, 0) <> COALESCE(:NEW.USER1_ID, 0)) OR(COALESCE(:OLD.USER2_ID, 0) <> COALESCE(:NEW.USER2_ID, 0)))) THEN RAISE_APPLICATION_ERROR(-20501, 'Document processed/posted') ; END IF;
IF (COALESCE(:OLD.C_BPartner_ID, 0)!=COALESCE(:NEW.C_BPartner_ID, 0)) OR (COALESCE(:OLD.M_PriceList_ID,0) != COALESCE(:NEW.M_PriceList_ID,0)) THEN SELECT COUNT(*) INTO v_n FROM C_INVOICELINE WHERE C_Invoice_ID = :NEW.C_Invoice_ID;
IF v_n>0 THEN RAISE_APPLICATION_ERROR(-20502, 'Cannot change bussiness partner or price list if there are lines') ; END IF; END IF;
IF(:OLD.Posted='Y' AND ((COALESCE(:OLD.DATEACCT, '01-01-1386') <> COALESCE(:NEW.DATEACCT, '01-01-1386')) OR(COALESCE(:OLD.C_CAMPAIGN_ID, 0) <> COALESCE(:NEW.C_CAMPAIGN_ID, 0)) OR(COALESCE(:OLD.C_PROJECT_ID, 0) <> COALESCE(:NEW.C_PROJECT_ID, 0)) OR(COALESCE(:OLD.C_ACTIVITY_ID, 0) <> COALESCE(:NEW.C_ACTIVITY_ID, 0)))) THEN RAISE_APPLICATION_ERROR(-20501, 'Document processed/posted') ; END IF; END IF; IF(INSERTING) THEN IF(:NEW.PROCESSED='Y') THEN RAISE_APPLICATION_ERROR(-20501, 'Document processed/posted') ; END IF; END IF; IF(DELETING) THEN IF(:OLD.PROCESSED='Y') THEN RAISE_APPLICATION_ERROR(-20501, 'Document processed/posted') ; END IF; END IF; END C_Invoice_Trg
