    v_ID NUMBER;
  v_RO         NUMBER;
  v_Processed  CHAR(1) ;
  v_C_ORDER_ID NUMBER(10) ;
  v_Docaction NVARCHAR2(2) ;
BEGIN
  IF INSERTING THEN
    v_C_ORDER_ID:=:new.C_ORDER_ID;
  ELSE
    v_C_ORDER_ID:=:old.C_ORDER_ID;
  END IF;
  SELECT PROCESSED,
    DOCACTION
  INTO v_Processed,
    v_Docaction
  FROM C_ORDER
  WHERE C_ORDER_ID=v_C_ORDER_ID;
  IF UPDATING THEN
    IF(v_Processed='Y' AND v_Docaction <> 'CL' AND ((COALESCE(:old.TAXBASEAMT, 0) <> COALESCE(:new.TAXBASEAMT, 0)) OR(COALESCE(:old.TAXAMT, 0) <> COALESCE(:new.TAXAMT, 0)) OR(COALESCE(:old.C_TAX_ID, 0) <> COALESCE(:new.C_TAX_ID, 0)))) THEN
      RAISE_APPLICATION_ERROR(-20501, 'Document processed/posted') ;
    END IF;
  END IF;
  NULL;
  IF((DELETING OR INSERTING) AND v_Processed='Y' AND v_Docaction <> 'CL') THEN
    RAISE_APPLICATION_ERROR(-20501, 'Document processed/posted') ;
  END IF;
  /** Is not needed
  -- Get ID
  IF (UPDATING OR INSERTING) THEN
  v_ID:=:new.C_Order_ID;
  ELSE
  v_ID:=:old.C_Order_ID;
  END IF;
  -- ReadOnly Check
  SELECT COUNT(*)
  INTO v_RO
  FROM C_Order
  WHERE C_Order_ID=v_ID
  AND DocAction <> 'CL' -- allow to close order
  AND (Processed='Y' OR Posted='Y');
  IF (v_RO > 0) THEN
  RAISE_APPLICATION_ERROR(-20501, 'Document processed/posted');
  END IF;
  **/
END C_OrderTax_Trg
