  v_Processed CHAR(1) ;
  v_C_ORDER_ID NUMBER(10) ;
  v_Docaction NVARCHAR2(2) ;
BEGIN
  IF INSERTING THEN
    v_C_ORDER_ID:=:NEW.C_ORDER_ID;
  ELSE
    v_C_ORDER_ID:=:OLD.C_ORDER_ID;
  END IF;
  SELECT PROCESSED,
    DOCACTION
  INTO v_Processed,
    v_Docaction
  FROM C_ORDER
  WHERE C_ORDER_ID=v_C_ORDER_ID;
  IF UPDATING THEN
    IF((COALESCE(:old.LINE, 0) <> COALESCE(:NEW.LINE, 0) OR COALESCE(:old.M_PRODUCT_ID, 0) <> COALESCE(:NEW.M_PRODUCT_ID, 0) OR COALESCE(:old.QTYORDERED, 0) <> COALESCE(:NEW.QTYORDERED, 0) OR COALESCE(:old.PRICELIST, 0) <> COALESCE(:NEW.PRICELIST, 0) OR COALESCE(:old.PRICEACTUAL, 0) <> COALESCE(:NEW.PRICEACTUAL, 0) OR COALESCE(:old.PRICELIMIT, 0) <> COALESCE(:NEW.PRICELIMIT, 0) OR COALESCE(:old.LINENETAMT, 0) <> COALESCE(:NEW.LINENETAMT, 0) OR COALESCE(:old.C_CHARGE_ID, 0) <> COALESCE(:NEW.C_CHARGE_ID, 0) OR COALESCE(:old.CHARGEAMT, 0) <> COALESCE(:NEW.CHARGEAMT, 0) OR COALESCE(:old.C_TAX_ID, 0) <> COALESCE(:NEW.C_TAX_ID, 0) OR COALESCE(:old.LOT, '.') <> COALESCE(:NEW.LOT, '.') OR COALESCE(:old.SERNO, '.') <> COALESCE(:NEW.SERNO, '.') OR COALESCE(:old.DIRECTSHIP, '.') <> COALESCE(:NEW.DIRECTSHIP, '.') OR COALESCE(:old.M_ATTRIBUTESETINSTANCE_ID, 0) <> COALESCE(:NEW.M_ATTRIBUTESETINSTANCE_ID, 0) OR COALESCE(:old.QUANTITYORDER, 0) <> COALESCE(:NEW.QUANTITYORDER, 0) OR COALESCE(
      :old.M_PRODUCT_UOM_ID, 0) <> COALESCE(:NEW.M_PRODUCT_UOM_ID, 0) )) THEN
      IF(v_Processed='Y' AND v_Docaction <> 'CL' AND ((COALESCE(:OLD.LINE, 0) <> COALESCE(:NEW.LINE, 0)) OR(COALESCE(:OLD.M_PRODUCT_ID, 0) <> COALESCE(:NEW.M_PRODUCT_ID, 0)) OR(COALESCE(:OLD.QTYORDERED, 0) <> COALESCE(:NEW.QTYORDERED, 0)) OR(COALESCE(:OLD.PRICELIST, 0) <> COALESCE(:NEW.PRICELIST, 0)) OR(COALESCE(:OLD.PRICEACTUAL, 0) <> COALESCE(:NEW.PRICEACTUAL, 0)) OR(COALESCE(:OLD.PRICELIMIT, 0) <> COALESCE(:NEW.PRICELIMIT, 0)) OR(COALESCE(:OLD.LINENETAMT, 0) <> COALESCE(:NEW.LINENETAMT, 0)) OR(COALESCE(:OLD.C_CHARGE_ID, 0) <> COALESCE(:NEW.C_CHARGE_ID, 0)) OR(COALESCE(:OLD.CHARGEAMT, 0) <> COALESCE(:NEW.CHARGEAMT, 0)) OR(COALESCE(:OLD.C_TAX_ID, 0) <> COALESCE(:NEW.C_TAX_ID, 0)) OR(COALESCE(:OLD.LOT, '.') <> COALESCE(:NEW.LOT, '.')) OR(COALESCE(:OLD.SERNO, '.') <> COALESCE(:NEW.SERNO, '.')) OR(COALESCE(:OLD.DIRECTSHIP, '.') <> COALESCE(:NEW.DIRECTSHIP, '.')) OR(COALESCE(:OLD.M_ATTRIBUTESETINSTANCE_ID, 0) <> COALESCE(:NEW.M_ATTRIBUTESETINSTANCE_ID, 0)) OR(COALESCE(
        :OLD.QUANTITYORDER, 0) <> COALESCE(:NEW.QUANTITYORDER, 0)) OR(COALESCE(:OLD.M_PRODUCT_UOM_ID, 0) <> COALESCE(:NEW.M_PRODUCT_UOM_ID, 0)))) THEN
        RAISE_APPLICATION_ERROR(-20501, 'Document processed/posted') ;
      END IF;
    END IF;
  END IF;
  IF((DELETING OR INSERTING) AND v_Processed='Y') THEN
    RAISE_APPLICATION_ERROR(-20501, 'Document processed/posted') ;
  END IF;
END C_OrdLine_Chk_Restrictions_Trg
