  v_Processed CHAR(1) ;
  v_Posted        CHAR(1) ;
  v_GL_JOURNAL_ID NUMBER(10) ;
BEGIN
  IF INSERTING THEN
    v_GL_JOURNAL_ID:=:new.GL_JOURNAL_ID;
  ELSE
    v_GL_JOURNAL_ID:=:old.GL_JOURNAL_ID;
  END IF;
  SELECT PROCESSED,
    POSTED
  INTO v_Processed,
    v_Posted
  FROM GL_JOURNAL
  WHERE GL_JOURNAL_ID=v_GL_JOURNAL_ID;
  IF(UPDATING) THEN
    IF((COALESCE(:old.AMTSOURCEDR, 0) <> COALESCE(:NEW.AMTSOURCEDR, 0) OR COALESCE(:old.AMTSOURCECR, 0) <> COALESCE(:NEW.AMTSOURCECR, 0) OR COALESCE(:old.C_CURRENCY_ID, 0) <> COALESCE(:NEW.C_CURRENCY_ID, 0))) THEN
      IF(v_Processed='Y' AND ((COALESCE(:old.LINE, 0) <> COALESCE(:new.LINE, 0)) OR (COALESCE(:old.C_DEBT_PAYMENT_ID, 0) <> COALESCE(:new.C_DEBT_PAYMENT_ID, 0)) OR(COALESCE(:old.AMTSOURCEDR, 0) <> COALESCE(:new.AMTSOURCEDR, 0)) OR(COALESCE(:old.AMTSOURCECR, 0) <> COALESCE(:new.AMTSOURCECR, 0)) OR(COALESCE(:old.C_CURRENCY_ID, 0) <> COALESCE(:new.C_CURRENCY_ID, 0)) OR(COALESCE(:old.C_UOM_ID, 0) <> COALESCE(:new.C_UOM_ID, 0)))) THEN
        RAISE_APPLICATION_ERROR(-20501, 'Document processed/posted') ;
      END IF;
      IF(v_Posted='Y' AND ((COALESCE(:old.DATEACCT, '01-01-1386') <> COALESCE(:new.DATEACCT, '01-01-1386')) OR(COALESCE(:old.C_VALIDCOMBINATION_ID, 0) <> COALESCE(:new.C_VALIDCOMBINATION_ID, 0)) OR(COALESCE(:old.AMTACCTDR, 0) <> COALESCE(:new.AMTACCTDR, 0)) OR(COALESCE(:old.AMTACCTCR, 0) <> COALESCE(:new.AMTACCTCR, 0)))) THEN
        RAISE_APPLICATION_ERROR(-20501, 'Document processed/posted') ;
      END IF;
    END IF;
  END IF;
  IF(DELETING AND v_Processed='Y') THEN
    RAISE_APPLICATION_ERROR(-20501, 'Document processed/posted') ;
  END IF;
  IF(INSERTING AND v_Processed='Y') THEN
    RAISE_APPLICATION_ERROR(-20501, 'Document processed/posted') ;
  END IF;
END GL_JL_Chk_Restrictions_Trg
