-- Variables
  v_ResultStr VARCHAR(2000):=''; 
  v_Message VARCHAR(2000):='';  
  v_Result NUMBER:=1; 
  v_expected_tax_diff NUMBER ;
  v_actual_tax_diff NUMBER ;
  v_description VARCHAR(2000) ;
  BEGIN --BODY
   select (coalesce(line_gross_amount,0) - linenetamt) , sum(taxamt) , description into v_expected_tax_diff , v_actual_tax_diff, v_description from c_orderline, c_orderlinetax where c_orderlinetax.c_orderline_id = c_orderline.c_orderline_id and c_orderline.c_orderline_id = p_orderline_id   group by (coalesce(line_gross_amount,0) - linenetamt), c_orderline.description;
   if ( v_actual_tax_diff - v_expected_tax_diff  != 0 ) then
        update c_orderlinetax set taxamt = taxamt - (v_actual_tax_diff - v_expected_tax_diff ) where c_orderlinetax_id  = ( select c_orderlinetax_id from c_orderlinetax where c_orderline_id = p_orderline_id order by taxamt desc limit 1) ;
   end if ;
   EXCEPTION
    WHEN OTHERS THEN
     v_ResultStr:= '@ERROR=' || SQLERRM;
     DBMS_OUTPUT.PUT_LINE(v_ResultStr) ;
  --ROLLBACK;
     RAISE;
END C_ORDERLINETAX_ROUNDING
