  IF(p_AD_Reference_ID = 10) THEN
    RETURN 'String    = 10';
  ELSIF(p_AD_Reference_ID = 11) THEN
    RETURN 'Integer   = 11';
  ELSIF(p_AD_Reference_ID = 12) THEN
    RETURN 'Amount    = 12';
  ELSIF(p_AD_Reference_ID = 13) THEN
    RETURN 'ID        = 13';
  ELSIF(p_AD_Reference_ID = 14) THEN
    RETURN 'Text      = 14';
  ELSIF(p_AD_Reference_ID = 15) THEN
    RETURN 'Date      = 15';
  ELSIF(p_AD_Reference_ID = 16) THEN
    RETURN 'DateTime  = 16';
  ELSIF(p_AD_Reference_ID = 17) THEN
    RETURN 'List      = 17';
  ELSIF(p_AD_Reference_ID = 18) THEN
    RETURN 'Table     = 18';
  ELSIF(p_AD_Reference_ID = 19) THEN
    RETURN 'TableDir  = 19';
  ELSIF(p_AD_Reference_ID = 20) THEN
    RETURN 'YesNo     = 20';
  ELSIF(p_AD_Reference_ID = 21) THEN
    RETURN 'Location  = 21';
  ELSIF(p_AD_Reference_ID = 22) THEN
    RETURN 'Number    = 22';
  ELSIF(p_AD_Reference_ID = 23) THEN
    RETURN 'Binary    = 23';
  ELSIF(p_AD_Reference_ID = 24) THEN
    RETURN 'Time      = 24';
  ELSIF(p_AD_Reference_ID = 25) THEN
    RETURN 'Account   = 25';
  ELSIF(p_AD_Reference_ID = 26) THEN
    RETURN 'RowID     = 26';
  ELSIF(p_AD_Reference_ID = 27) THEN
    RETURN 'Color     = 27';
  ELSIF(p_AD_Reference_ID = 28) THEN
    RETURN 'Button    = 28';
  ELSIF(p_AD_Reference_ID = 29) THEN
    RETURN 'Quantity  = 29';
  ELSIF(p_AD_Reference_ID = 30) THEN
    RETURN 'Search    = 30';
  ELSIF(p_AD_Reference_ID = 31) THEN
    RETURN 'Locator   = 31';
  ELSIF(p_AD_Reference_ID = 32) THEN
    RETURN 'Image     = 32';
  ELSIF(p_AD_Reference_ID = 33) THEN
    RETURN 'Assignment= 33';
  ELSE
    RETURN 'Unknown ('||p_AD_Reference_ID||')';
  END IF;
END DBA_DISPLAYTYPE
