  v_product_id     M_Transaction.M_Product_ID%TYPE;
  v_quantity       NUMBER;
  v_costamt        NUMBER;
  --TYPE RECORD IS REF CURSOR;
  --  Cur_Cost RECORD;
BEGIN
  SELECT sum(cost) INTO v_costamt
  FROM m_transaction_cost
  WHERE m_transaction_id = p_transaction_id
    AND costdate <= p_date;
  IF (v_costamt IS NOT NULL) THEN
    RETURN v_costamt;
  END IF;

  SELECT m_product_id, movementqty
    INTO v_product_id, v_quantity
  FROM M_Transaction
  WHERE m_transaction_id = p_transaction_id;

  RETURN m_get_product_cost(v_product_id, p_date, 'AV') * v_quantity;
END M_GET_TRANSACTION_COST
