function.body:
/****************************************************************************** * The contents of this file are subject to the Compiere License Version 1.1 * ("License"); You may not use this file except in compliance with the License * You may obtain a copy of the License at http://www.compiere.org/license.html * Software distributed under the License is distributed on an "AS IS" basis, * WITHOUT WARRANTY OF ANY KIND, either express or implied. See the License for * the specific language governing rights and limitations under the License. * The Original Code is Compiere ERP & Business Solution * The Initial Developer of the Original Code is Jorg Janke and ComPiere, Inc. * Portions created by Jorg Janke are Copyright (C) 1999-2001 Jorg Janke, parts * created by ComPiere are Copyright (C) ComPiere, Inc.; All Rights Reserved. * Contributor(s): Openbravo SLU * Contributions are Copyright (C) 2012 Openbravo S.L.U. ******************************************************************************/ v_nextLineNum NUMBER; v_newUnitPrice NUMBER; v_newGrossAmt NUMBER; v_promotionDiscounted NUMBER; v_client VARCHAR(32); v_org VARCHAR(32); BEGIN if (p_taxIncluded = 'Y') then v_newUnitPrice := p_newUnitPrice; v_newGrossAmt := p_newGrossAmt; v_promotionDiscounted := p_basePrice - v_newUnitPrice; else v_newUnitPrice := 0; v_newGrossAmt := 0; v_promotionDiscounted := p_basePrice - p_newNetLine; end if;
if (p_type ='O') then select coalesce(max(line), 0) + 10 into v_nextLineNum from c_orderline_offer where c_orderline_id = p_line_id;
select ad_client_id, ad_org_id into v_client, v_org from c_orderline where c_orderline_id = p_line_id;
update c_orderline set gross_unit_price = round(v_newUnitPrice, p_priceprecision), line_gross_amount = round(v_newGrossAmt, p_priceprecision), priceactual = round(p_newNetLine, p_priceprecision), linenetamt = round(p_newNetAmt, p_priceprecision) where c_orderline_id = p_line_id;
if (p_taxIncluded = 'Y') then insert into c_orderline_offer (c_orderline_offer_id, ad_client_id, ad_org_id, isactive, createdby, updatedby, c_orderline_id, line, m_offer_id, priceoffergross, amtoffer, totalamt, displayedtotalamt) values (get_uuid(), v_client, v_org, 'Y', p_user_id, p_user_id, p_line_id, v_nextLineNum, p_rule_id, p_basePrice, round(v_promotionDiscounted, p_priceprecision), round(p_total_amt, p_priceprecision), round(p_displayed_amt, p_priceprecision)); else insert into c_orderline_offer (c_orderline_offer_id, ad_client_id, ad_org_id, isactive, createdby, updatedby, c_orderline_id, line, m_offer_id, priceoffer, amtoffer, totalamt, displayedtotalamt) values (get_uuid(), v_client, v_org, 'Y', p_user_id, p_user_id, p_line_id, v_nextLineNum, p_rule_id, p_basePrice, round(v_promotionDiscounted, p_priceprecision), round(p_total_amt, p_priceprecision), round(p_displayed_amt, p_priceprecision)); end if; else select coalesce(max(line), 0) + 10 into v_nextLineNum from c_invoiceline_offer where c_invoiceline_id = p_line_id;
select ad_client_id, ad_org_id into v_client, v_org from c_invoiceline where c_invoiceline_id = p_line_id;
update c_invoiceline set gross_unit_price = round(v_newUnitPrice, p_priceprecision), line_gross_amount = round(v_newGrossAmt, p_priceprecision), priceactual = round(p_newNetLine, p_priceprecision), linenetamt = round(p_newNetAmt, p_priceprecision) where c_invoiceline_id = p_line_id;
if (p_taxIncluded = 'Y') then insert into c_invoiceline_offer (c_invoiceline_offer_id, ad_client_id, ad_org_id, isactive, createdby, updatedby, c_invoiceline_id, line, m_offer_id, priceoffergross, amtoffer, totalamt, displayedtotalamt) values (get_uuid(), v_client, v_org, 'Y', p_user_id, p_user_id, p_line_id, v_nextLineNum, p_rule_id, p_basePrice, round(v_promotionDiscounted, p_priceprecision), round(p_total_amt, p_priceprecision), round(p_displayed_amt, p_priceprecision)); else insert into c_invoiceline_offer (c_invoiceline_offer_id, ad_client_id, ad_org_id, isactive, createdby, updatedby, c_invoiceline_id, line, m_offer_id, priceoffer, amtoffer, totalamt, displayedtotalamt) values (get_uuid(), v_client, v_org, 'Y', p_user_id, p_user_id, p_line_id, v_nextLineNum, p_rule_id, p_basePrice, round(v_promotionDiscounted, p_priceprecision), round(p_total_amt, p_priceprecision), round(p_displayed_amt, p_priceprecision)); end if; end if; END M_PROMOTION_ADD
