  v_fixed NUMBER;
  v_add NUMBER;
  v_discount NUMBER;
  v_qty_From NUMBER;
  v_qty_To NUMBER;
  v_apply_next VARCHAR(1);
  v_tax VARCHAR(32);
  v_qty NUMBER;
  v_unitPrice NUMBER;
  v_newUnitPrice NUMBER;
  v_newGrossAmt NUMBER;
  v_newNetAmt NUMBER;
  v_newNetLine NUMBER;
  v_priceactual NUMBER;
  v_basePrice NUMBER;
BEGIN
  select fixed, coalesce(addamt, 0), coalesce(discount, 0),
         qty_From, qty_To, apply_next
    into v_fixed, v_add, v_discount,
         v_qty_From, v_qty_To, v_apply_next
    from m_offer
   where m_offer_id = p_rule_id;

  if (p_type ='O') then
		select gross_unit_price, c_tax_id, qtyordered, priceactual
			into v_unitprice, v_tax, v_qty, v_priceactual
			from c_orderline
		 where c_orderline_id = p_line_id;
  else 
	 select gross_unit_price, c_tax_id, qtyinvoiced, priceactual
		 into v_unitprice, v_tax, v_qty, v_priceactual
		 from c_invoiceline
		where c_invoiceline_id = p_line_id;
  end if;

  if (v_qty_From is not null and v_qty < v_qty_From) or (v_qty_To is not null and v_qty > v_qty_To) then
    return 'Y'; -- rule not applied, apply next one if present
  end if;

  if (p_taxIncluded = 'Y') then
    if (v_fixed is not null) then
      v_newUnitPrice := v_fixed;
    else
      v_newUnitPrice := (v_unitprice - v_add) * (1 - v_discount / 100);
    end if;

    v_newGrossAmt := v_newUnitPrice * v_qty;

    v_newNetLine := c_get_net_price_from_gross(v_tax, v_newGrossAmt, v_newGrossAmt, p_priceprecision, v_qty);
    v_newNetAmt := c_get_net_price_from_gross(v_tax, v_newGrossAmt,v_newGrossAmt, p_priceprecision, 1);
    v_basePrice := v_unitprice;
  else
    if (v_fixed is not null) then
      v_newNetLine := v_fixed;
    else
      v_newNetLine := (v_priceactual - v_add) * (1 - v_discount / 100);
    end if;

    v_newNetAmt := v_newNetLine * v_qty;
    v_basePrice := v_priceactual;
  end if;

  M_PROMOTION_ADD(p_type, p_line_id, p_rule_id, p_taxIncluded, p_priceprecision, v_newUnitPrice, v_newGrossAmt, v_newNetLine, v_newNetAmt, v_basePrice, p_user_id);

  return v_apply_next;
END M_PROMOTION_ADJUSTMENT
