p_record_id VARCHAR(60);
p_message VARCHAR(2000);
p_docAction VARCHAR(60);
p_user VARCHAR(60);
p_result NUMBER;

v_RefInventory_ID VARCHAR2(32);

TYPE RECORD IS REF CURSOR;
Cur_Params RECORD;
Cur_MOVEMENTLINE RECORD;

BEGIN
  FOR Cur_Params IN (
    SELECT *
	  FROM ad_ep_instance_para
	  WHERE ad_ep_instance_id = p_ep_instance
  ) LOOP
    IF (cur_params.parametername LIKE 'DocAction') THEN
	  p_docaction := Cur_Params.p_string;
	ELSIF (cur_params.parametername LIKE 'Record_ID') THEN
	  p_record_id := cur_params.p_string;
	ELSIF (cur_params.parametername LIKE 'User') THEN
	  p_user := cur_params.p_string;
	ELSIF (cur_params.parametername LIKE 'Message') THEN
	  p_message := cur_params.p_text;
	ELSIF (cur_params.parametername LIKE 'Result') THEN
	  p_result := cur_params.p_number;
	END IF;
  END LOOP;

  FOR Cur_MOVEMENTLINE IN (
	SELECT *
	  FROM M_MOVEMENTLINE
	  WHERE M_MOVEMENT_ID = p_record_id
  ) LOOP

    IF (COALESCE(Cur_MOVEMENTLINE.m_attributesetinstance_id, '0') <> COALESCE(Cur_MOVEMENTLINE.m_attributesetinstanceto_id,'0')) THEN
	  SELECT M_REFINVENTORY_ID INTO v_RefInventory_ID
	    FROM m_attributesetinstance
	    WHERE m_attributesetinstance_id = Cur_MOVEMENTLINE.m_attributesetinstanceto_id;

	  UPDATE M_REFINVENTORY
	    SET UPDATEDBY = p_user,
	    UPDATED = now()
	    WHERE M_REFINVENTORY_ID = v_RefInventory_ID;

    END IF;
  END LOOP;
END M_REFINV_UPDATE_LASTMODIFIED
