v_ExistsNegativeStock NUMBER;
BEGIN
IF AD_isTriggerEnabled()='N' THEN RETURN; END IF; IF (:OLD.ALLOWNEGATIVESTOCK='Y' AND :NEW.ALLOWNEGATIVESTOCK='N') THEN SELECT COUNT(*) INTO v_ExistsNegativeStock FROM ( SELECT ROUND(COALESCE(QTYONHAND, 0), 2) AS QTY, ROUND(COALESCE(QTYORDERONHAND, 0), 2) AS QTYORDER FROM M_STORAGE_DETAIL WHERE AD_CLIENT_ID=:NEW.AD_Client_ID ) A WHERE QTY<0 OR QTYORDER<0; IF v_ExistsNegativeStock<>0 THEN RAISE_APPLICATION_ERROR(-20000, '@NegativeStockCheck@'); END IF; END IF;
END AD_CLIENTINFO_TRG
