  numModules NUMBER;
  numPrefixes NUMBER;

BEGIN

    IF AD_isTriggerEnabled()='N' THEN RETURN;
    END IF;


  select count(ad_module_id) into numModules from ad_module where ad_module_id<>'0';

  select count(ad_module_id) into numPrefixes from ad_module_dbprefix where ad_module_id<>'0';

  IF (INSERTING) THEN
    IF (numPrefixes>numModules) THEN
        RAISE NO_DATA_FOUND;
    END IF;
  END IF;

END AD_MODULE_DBPREFIX_UNIQUE_TRG
