IF AD_isTriggerEnabled()='N' THEN IF DELETING THEN RETURN OLD; ELSE RETURN NEW; END IF; END IF;
SELECT instance_purpose INTO v_InstancePurpose FROM ad_system_info;
IF :new.isindevelopment = 'Y' AND v_InstancePurpose = 'P' THEN RAISE_APPLICATION_ERROR(-20000, '@AD_MODULE_INDEV_IN_PROD@'); END IF;
IF DELETING THEN RETURN OLD; ELSE RETURN NEW; END IF;
END AD_MODULE_NODEV_IN_PROD_TRG
