BEGIN
IF AD_isTriggerEnabled()='N' THEN RETURN; END IF;
IF (UPDATING) THEN IF NOT((COALESCE(:OLD.UserLevel,'.') <> COALESCE(:NEW.UserLevel,'.')) OR (:OLD.IsManual = 'Y' AND :NEW.IsManual='N')) THEN RETURN; END IF; END IF;
IF (INSERTING) THEN NULL; ELSIF (:new.IsManual = 'Y') THEN RETURN; END IF;
END AD_ROLE_TRG
