Cur_Defaults A_Asset_Group_Acct%ROWTYPE; v_Acct_ID NUMBER; BEGIN -- Default Accounts for all AcctSchema IF(INSERTING) THEN SELECT * into cur_Defaults FROM A_Asset_Group_Acct d1 WHERE d1.A_Asset_Group_ID=:new.A_Asset_Group_ID;
-- Customer Ad_Sequence_Next('A_Asset_Acct', Cur_Defaults.AD_Client_ID, v_Acct_ID) ; INSERT INTO A_ASSET_ACCT ( A_ASSET_ID, C_ACCTSCHEMA_ID, AD_CLIENT_ID, AD_ORG_ID, ISACTIVE, CREATED, CREATEDBY, UPDATED, UPDATEDBY, A_DEPRECIATION_ACCT, A_ACCUMDEPRECIATION_ACCT, A_DISPOSAL_LOSS, A_DISPOSAL_GAIN ) VALUES ( :new.A_Asset_ID, Cur_Defaults.C_AcctSchema_ID, :new.AD_Client_ID, :new.AD_Org_ID, 'Y', now(), :new.CreatedBy, now(), :new.UpdatedBy, Cur_Defaults.A_DEPRECIATION_ACCT, Cur_Defaults.A_ACCUMDEPRECIATION_ACCT, Cur_Defaults.A_DISPOSAL_LOSS, Cur_Defaults.A_DISPOSAL_LOSS ) ; -- Vendor END IF; EXCEPTION WHEN NO_DATA_FOUND THEN RAISE_APPLICATION_ERROR(-20008, 'The asset group for this asset has no accounts') ; END A_ASSET_TRG
