v_C_ORDERLINE_ID C_ORDERLINE.C_ORDERLINE_ID%TYPE;
v_ISTAXINCLUDED M_PRICELIST.ISTAXINCLUDED%TYPE;
v_STDPRECISION C_CURRENCY.STDPRECISION%TYPE;
v_PRICEPRECISION C_CURRENCY.PRICEPRECISION%TYPE;
V_M_PRICELIST_ID M_PRICELIST.M_PRICELIST_ID%TYPE;
V_DATEORDERED C_ORDER.DATEORDERED%TYPE;
V_PRICELIST M_PRODUCTPRICE.PRICELIST%TYPE;
V_M_PRODUCT_ID M_PRODUCT.M_PRODUCT_ID%TYPE;
V_SERVICEAMOUNT NUMBER;

BEGIN
  IF (AD_isTriggerEnabled()='N') THEN
    RETURN;
  END IF;

  IF (INSERTING OR UPDATING) THEN
    v_C_ORDERLINE_ID := :NEW.C_ORDERLINE_ID;
  ELSE
    v_C_ORDERLINE_ID := :OLD.C_ORDERLINE_ID;
  END IF;

  SELECT ISTAXINCLUDED, C_CURRENCY.STDPRECISION, PRICEPRECISION, M_PRICELIST_ID, C_ORDER.DATEORDERED, C_ORDERLINE.M_PRODUCT_ID
  INTO v_ISTAXINCLUDED, v_STDPRECISION, v_PRICEPRECISION, v_M_PRICELIST_ID, v_DATEORDERED, V_M_PRODUCT_ID
  FROM C_ORDER, C_ORDERLINE, C_CURRENCY
  WHERE C_ORDER.C_ORDER_ID = C_ORDERLINE.C_ORDER_ID
  AND C_ORDERLINE.C_ORDERLINE_ID = v_C_ORDERLINE_ID
  AND C_ORDER.C_CURRENCY_ID = C_CURRENCY.C_CURRENCY_ID;

	--Get Service price for selected pricelist
	SELECT PRICELIST 
	INTO v_PriceList
	FROM M_PRODUCTPRICE
	WHERE M_PRICELIST_VERSION_ID = M_GET_PRICELIST_VERSION(v_M_PRICELIST_ID, v_Dateordered)
	AND M_PRODUCT_ID = v_M_Product_ID;
			DBMS_OUTPUT.PUT_LINE('v_PriceList ' || v_PriceList) ;
	SELECT coalesce(C_GET_SERVICE_AMOUNT(V_C_ORDERLINE_ID),0) INTO V_SERVICEAMOUNT;

    IF(v_ISTAXINCLUDED = 'Y') THEN
      UPDATE C_ORDERLINE SET GROSS_UNIT_PRICE = round(v_PriceList + (V_SERVICEAMOUNT / QTYORDERED) ,v_PRICEPRECISION) , LINE_GROSS_AMOUNT = round(V_SERVICEAMOUNT + (v_PriceList * QTYORDERED), v_STDPRECISION)
      WHERE C_ORDERLINE_ID = v_C_ORDERLINE_ID;
    ELSE
      UPDATE C_ORDERLINE SET PRICEACTUAL = round(v_PriceList + (V_SERVICEAMOUNT / QTYORDERED) ,v_PRICEPRECISION) , LINENETAMT = round(V_SERVICEAMOUNT + (v_PriceList * QTYORDERED), v_STDPRECISION)
      WHERE C_ORDERLINE_ID = v_C_ORDERLINE_ID;
    END IF;
END C_ORDERLINE_SERVREL_TRG
