v_C_ORDERLINE_ID C_ORDERLINE.C_ORDERLINE_ID%TYPE;
v_ISTAXINCLUDED M_PRICELIST.ISTAXINCLUDED%TYPE;

BEGIN
  IF (AD_isTriggerEnabled()='N') THEN
    RETURN;
  END IF;

  IF (INSERTING OR UPDATING) THEN
    v_C_ORDERLINE_ID := :NEW.C_ORDERLINE_ID;
  ELSE
    v_C_ORDERLINE_ID := :OLD.C_ORDERLINE_ID;
  END IF;

  SELECT ISTAXINCLUDED INTO v_ISTAXINCLUDED
  FROM C_ORDER, C_ORDERLINE
  WHERE C_ORDER.C_ORDER_ID = C_ORDERLINE.C_ORDER_ID
  AND C_ORDERLINE.C_ORDERLINE_ID = v_C_ORDERLINE_ID;

  IF (INSERTING) THEN
    IF(v_ISTAXINCLUDED = 'Y') THEN
      UPDATE C_ORDERLINE SET GROSS_UNIT_PRICE = GROSS_UNIT_PRICE + :NEW.AMOUNT WHERE C_ORDERLINE_ID = v_C_ORDERLINE_ID;
    ELSE
      UPDATE C_ORDERLINE SET PRICEACTUAL = PRICEACTUAL + :NEW.AMOUNT WHERE C_ORDERLINE_ID = v_C_ORDERLINE_ID;
    END IF;
  END IF;
  IF (UPDATING) THEN
    IF(v_ISTAXINCLUDED = 'Y') THEN
      UPDATE C_ORDERLINE SET GROSS_UNIT_PRICE = GROSS_UNIT_PRICE + :NEW.AMOUNT - :OLD.AMOUNT WHERE C_ORDERLINE_ID = v_C_ORDERLINE_ID;
    ELSE
      UPDATE C_ORDERLINE SET PRICEACTUAL = PRICEACTUAL + :NEW.AMOUNT - :OLD.AMOUNT WHERE C_ORDERLINE_ID = v_C_ORDERLINE_ID;
    END IF;
  END IF;
  IF (DELETING) THEN
    IF(v_ISTAXINCLUDED = 'Y') THEN
      UPDATE C_ORDERLINE SET GROSS_UNIT_PRICE = GROSS_UNIT_PRICE - :OLD.AMOUNT WHERE C_ORDERLINE_ID = v_C_ORDERLINE_ID;
    ELSE
      UPDATE C_ORDERLINE SET PRICEACTUAL = PRICEACTUAL - :OLD.AMOUNT WHERE C_ORDERLINE_ID = v_C_ORDERLINE_ID;
    END IF;
  END IF;
END C_ORDERLINE_SERVREL_TRG
