trigger.body:
/************************************************************************* * The contents of this file are subject to the Compiere Public * License 1.1 ("License"); You may not use this file except in * compliance with the License. You may obtain a copy of the License in * the legal folder of your Openbravo installation. * Software distributed under the License is distributed on an * "AS IS" basis, WITHOUT WARRANTY OF ANY KIND, either express or * implied. See the License for the specific language governing rights * and limitations under the License. * The Original Code is Compiere ERP & Business Solution * The Initial Developer of the Original Code is Jorg Janke and ComPiere, Inc. * Portions created by Jorg Janke are Copyright (C) 1999-2001 Jorg Janke, * parts created by ComPiere are Copyright (C) ComPiere, Inc.; * All Rights Reserved. * Contributor(s): Openbravo SLU * Contributions are Copyright (C) 2001-2012 Openbravo, S.L.U. * * Specifically, this derivative work is based upon the following Compiere * file and version. */ v_Tree_ID VARCHAR2(32); v_Parent_ID VARCHAR2(32); v_count NUMBER;
BEGIN
IF AD_isTriggerEnabled()='N' THEN RETURN; END IF;
IF (INSERTING) THEN -- Create TreeNode -- -- get AD_Tree_ID + ParentID SELECT c.AD_Tree_User2_ID, n.Node_ID INTO v_Tree_ID, v_Parent_ID FROM AD_ClientInfo c, AD_TreeNode n WHERE c.AD_Tree_User2_ID=n.AD_Tree_ID AND n.Parent_ID IS NULL AND c.AD_Client_ID=:new.AD_Client_ID; -- DBMS_OUTPUT.PUT_LINE('Tree='||xTree_ID||' Node='||:new.C_Project_ID||' Parent='||xParent_ID); -- Insert into TreeNode INSERT INTO AD_TreeNode ( AD_TreeNode_ID, AD_Client_ID, AD_Org_ID, IsActive, Created, CreatedBy, Updated, UpdatedBy, AD_Tree_ID, Node_ID, Parent_ID, SeqNo ) VALUES ( get_uuid(), :new.AD_Client_ID, :new.AD_Org_ID, :new.IsActive, :new.Created, :new.CreatedBy, :new.Updated, :new.UpdatedBy, v_Tree_ID, :new.C_User2_ID, v_Parent_ID,( CASE :new.IsSummary WHEN 'Y' THEN 100 ELSE 999 END ) );
ELSIF (DELETING) THEN -- Delete TreeNode -- -- get AD_Tree_ID SELECT c.AD_Tree_User2_ID INTO v_Tree_ID FROM AD_ClientInfo c WHERE c.AD_Client_ID=:old.AD_Client_ID;
SELECT COUNT(*) INTO v_count FROM AD_TREENODE WHERE parent_id=:old.C_User2_ID AND AD_TREE_ID=v_Tree_ID;
IF(v_count>0) THEN RAISE_APPLICATION_ERROR(-20000, '@DimensionHasChildren@'); END IF;
DELETE FROM AD_TREENODE WHERE AD_CLIENT_ID=:old.AD_Client_ID AND AD_Tree_ID=v_Tree_ID AND Node_ID=:old.C_User2_ID; END IF; END C_USER2_TRG
