v_Count NUMBER;
BEGIN
IF AD_isTriggerEnabled()='N' THEN RETURN; END IF;
SELECT MAX(A.NUM) INTO v_Count FROM ( SELECT COUNT(*) AS NUM FROM c_orderline ol LEFT JOIN m_inoutline iol ON ol.c_orderline_id = iol.c_orderline_id GROUP BY ol.qtyordered, iol.c_orderline_id HAVING SUM(iol.movementqty) > ol.qtyordered) A;
IF(v_Count <> 0) THEN RAISE_APPLICATION_ERROR(-20000, '@MovementQtyCheck@'); END IF;
END M_INOUTLINE_TRG2
