v_MovementDate timestamp without time zone;
BEGIN
IF AD_isTriggerEnabled()='N' THEN RETURN; END IF;
IF (INSERTING) THEN SELECT MOVEMENTDATE INTO v_MovementDate FROM M_PRODUCTION WHERE M_PRODUCTION_ID = :NEW.M_PRODUCTION_ID;
-- RAISE_APPLICATION_ERROR(-20000, v_MovementDate);
:NEW.MOVEMENTDATE := v_MovementDate; END IF;
END M_PRODUCTIONPLAN_TRG2
