    v_Display VARCHAR2(4000) ;
    v_Column  VARCHAR2(2000) ;
  BEGIN
    v_Column:=p_ColumnName;
    IF p_IsData='Y' THEN
      v_Column:='''' || v_Column || '''';
    END IF;
    IF p_Reference_ID=11 THEN -- INTEGERS
      v_Display:='TRIM(TO_CHAR(' || v_Column || ', ''9999999990''))';
    ELSIF p_Reference_ID IN(12, 22, 29, 800008, 800019) THEN -- NUMBER
      v_Display:='TRIM(TO_CHAR(ROUND(' || v_Column || ', 2), ''9999999990.09''))';
    ELSIF p_Reference_ID=15 THEN -- DATE
      v_Display:='TO_CHAR(' || v_Column || ', ''DD-MM-YYYY'')';
    ELSIF p_Reference_ID=24 THEN -- TIME
      v_Display:='TO_CHAR(' || v_Column || ', ''HH24:MI:SS'')';
    ELSIF p_Reference_ID=16 THEN -- DATETIME
      v_Display:='TO_CHAR(' || v_Column || ', ''DD-MM-YYYY HH24:MI:SS'')';
    ELSE
      v_Display:=v_Column;
    END IF;
    RETURN v_Display;
EXCEPTION
  WHEN OTHERS THEN
    RETURN v_Column;
END AD_COLUMN_FORMAT
