  v_varchar2 VARCHAR2(32767) ;
  TYPE RECORD IS REF CURSOR;
    cur_view RECORD;
BEGIN
    FOR cur_view IN
      (SELECT TEXT
      FROM DBA_VIEWS
      WHERE OWNER=p_owner
        AND VIEW_NAME=p_viewName
      )
    LOOP
      v_varchar2 := cur_view.TEXT;
      v_varchar2 := TRIM(SUBSTR(v_varchar2, 1, 4000)) ;
      EXIT;
    END LOOP;
    RETURN(v_varchar2) ;
END DBA_VIEW_BODY
