  TYPE ArrayPesos IS VARRAY(10) OF INTEGER;
  v_pesos ArrayPesos;
  v_dc1      NUMBER:=0;
  v_dc2      NUMBER:=0;
  v_digito   NUMBER;
  v_contador NUMBER:=8;
BEGIN
  v_pesos:=ArrayPesos(1, 2, 4, 8, 5, 10, 9, 7, 3, 6) ;
  WHILE(v_contador > 0)
  LOOP
    v_digito:=SUBSTR(p_entidadOficina, v_contador, 1) ;
    v_dc1:=v_dc1 + v_pesos(v_contador+2) *v_digito;
    v_contador:=v_contador - 1;
  END LOOP;
  v_dc1:=11 - MOD(v_dc1, 11) ;
  IF(v_dc1=10) THEN
    v_dc1:=1;
  ELSIF(v_dc1=11) THEN
    v_dc1:=0;
  END IF;
  v_contador:=10;
  WHILE(v_contador > 0)
  LOOP
    v_digito:=SUBSTR(p_numeroCuenta, v_contador, 1) ;
    v_dc2:=v_dc2 + v_pesos(v_contador) *v_digito;
    v_contador:=v_contador - 1;
  END LOOP;
  v_dc2:=11 - MOD(v_dc2, 11) ;
  IF(v_dc1=10) THEN
    v_dc2:=1;
  ELSIF(v_dc1=11) THEN
    v_dc2:=0;
  END IF;
  RETURN(TO_CHAR(v_dc1) ||TO_CHAR(v_dc2)) ;
END DIGIT_CONTROL_ACCOUNT
