sted'); END IF; END IF;
IF (INSERTING) THEN IF (:NEW.processed = 'Y') THEN RAISE_APPLICATION_ERROR (-20501, 'Document processed/posted'); END IF; END IF;
IF (DELETING) THEN IF (:OLD.processed = 'Y') THEN RAISE_APPLICATION_ERROR (-20501, 'Document processed/posted'); END IF; END IF; END c_remittance_trg
